def hex_to_rgb(hex):
    r = int(hex[1:3], 16)
    g = int(hex[3:5], 16)
    b = int(hex[5:7], 16)
    return r,g,b

def percentage_to_float(percent):
    # percent (0~100) mapping to (1.0 ~ 0.0)
    return format(1.0 - percent/100, '.2f')

